import React, {Component} from 'react';
import Burger from '../../components/Burger/Burger';
import BuildControls from '../../components/BuildControls/BuildControls'

const INGREDIENTS_PRICES = {
    salad: 0.5,
    bacon: 0.7,
    cheese: 0.4,
    meat: 1.3
};

class BurgerBuilder extends Component {

    state = {
        ingredients: {
            salad: 0,
            bacon: 0,
            cheese: 0,
            meat: 0
        },
        totalPrices: 4
    }

    addIngredientsHandler = (type) => {
            // Handling count
            const oldCount = this.state.ingredients[type];
            const updatedCount = oldCount + 1;
            const updatedIngredients = {
                ...this.state.ingredients
            };
            updatedIngredients[type] = updatedCount;

            // Handling price
            const oldPrice = this.state.totalPrices;
            const priceAddition = INGREDIENTS_PRICES[type];
            const updatedPrice = oldPrice + priceAddition;

            // Set state
            this.setState({totalPrices: updatedPrice, ingredients: updatedIngredients})
    }

    removeIngredientsHandler = (type) => {
        // Handling count
        const oldCount = this.state.ingredients[type];
        const updatedCount = oldCount - 1;
        const updatedIngredients = {
            ...this.state.ingredients
        };
        updatedIngredients[type] = updatedCount;

        // Handling price
        const oldPrice = this.state.totalPrices;
        const priceAddition = INGREDIENTS_PRICES[type];
        const updatedPrice = oldPrice - priceAddition;

        // Set state
        this.setState({totalPrices: updatedPrice, ingredients: updatedIngredients})
    }

    render(){

        const disabledIngredient = {
            ...this.state.ingredients
        };

        return(
            <React.Fragment>
                <Burger ingredients={this.state.ingredients}></Burger>
                <BuildControls add={this.addIngredientsHandler} remove={this.removeIngredientsHandler}></BuildControls>
            </React.Fragment>
        );
    }

}

export default BurgerBuilder
